!function(){tinymce.PluginManager.add("mce_content_reveal",function(e){e.addButton("mce_content_reveal",{title:"Content Reveal",icon:"icon dashicons-image-flip-vertical",onclick:function(){selectText=tinymce.activeEditor.selection.getContent({format:"text"}),e.insertContent('[reveal heading="%image% Click here to show/hide contents"]'+selectText+"[/reveal]")}})})}();